mapper.select:
select B.EMP_NAME, B.EMP_NO, (select com_name from common where com_code = B.emp_dept) EMP_DEPT , (select com_name from common where com_code = B.emp_job) EMP_JOB, B.EMP_PROFILE, B.RNUM from ( select a.*, row_number() over (order by a.emp_name asc) as rnum from ( select emp_name, emp_no, emp_dept, emp_job, att.file_savepath emp_profile from emp emp, (select file_no, file_savepath from attach ) att where 1=1 and emp.emp_profile = att.file_no(+) and emp.emp_stat != '퇴직완료' order by emp_name asc ) a where 1=1
and a.emp_dept like '%' || #{keyword} || '%'
) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

SELECT b.emp_name, b.emp_stat, b.emp_no, b.emp_date, b.empt_end_date, b.k_date as empKdate, b.emp_work FROM ( SELECT a.*, ROW_NUMBER() OVER (ORDER BY a.emp_name asc) rnum FROM ( SELECT DISTINCT emp.emp_name, emp.emp_stat, emp.emp_no, emp.emp_date, empt.empt_end_date,(trunc(sysdate)-trunc(emp.emp_date)) as k_date, emp.emp_work FROM emp LEFT JOIN empt ON emp.emp_no = empt.emp_no WHERE 1=1
and emp_dept like '%' || #{keyword} || '%'
AND emp.emp_stat != '퇴직완료'
) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.update:
update emp
set
	emp_stat = #{empStat}
where emp_no = #{empNo}

mapper.select:
select * from ( select a.*, row_number() over (order by a.emp_name asc) as rnum from ( select dd.*, case when (select com_name from common where com_code = dd.hr_history) in ('직책 없음', '파트장', '팀장', '실장', '본부장', 'CFO', 'CEO') then '' else (select com_name from common where com_code = dd.hr_history) end as hr_history_nm from (select a.emp_name, a.emp_no, b.com_name as emp_dept, c.com_name as emp_pos, cc.hr_history from (select * from emp where emp_stat != '퇴직완료') a left outer join common b on a.emp_dept = b.com_code left outer join common c on a.emp_pos = c.com_code left outer join (select bb.emp_no, bb.hr_history from (select emp_no, max(hr_no) max_hr_no from hr_detail group by emp_no) aa, hr_detail bb where aa.max_hr_no = bb.hr_no) cc on a.emp_no = cc.emp_no ) dd order by emp_name ) a where 1=1
) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

