mapper.select:
	select
         b.*
      from(
         select
            a.*, row_number() over (order by a.hr_code desc) rnum
         from(
            select 
                hr_code, hr_type, hr_resp, hr_date, hr_memo
            from hr_hx
            where 1=1
            
            order by hr_date desc
         ) a
      ) b
	
		where b.rnum >= #{startRow} and b.rnum <= #{endRow}

