mapper.insert:

	select todolist_seq.nextval from dual

INSERT INTO todolist (
    todo_no, emp_no, todo_content
) VALUES (
    #{todoNo}, #{empNo}, #{todoContent}
)

mapper.delete:
DELETE FROM todolist
WHERE emp_no = #{empNo}
AND todo_content = #{todoContent}
