mapper.select:
select
	b.*
from(
	select
		a.*, row_number() over (order by a.cus_rnum desc) as r_num 
	from(
		select
			cus_rnum, cus_pw, cus_com, cus_name, cus_logo, cus_addr, cus_addr2, cus_zip,
			cus_tel, cus_emp, cus_email, cus_birth, cus_image, cus_signature
		from customer
		where not cus_rnum in 'admin' 
		and 1=1
		
		order by cus_rnum desc
	) a
) b

	where b.r_num >= #{startRow} and b.r_num <= #{endRow}

mapper.insert:
insert into func_info(
	func_name, func_memo, func_fee, func_explain
)values(
	#{funcName}, #{funcMemo}, #{funcFee}, #{funcExplain}
)

mapper.update:
update func_info
set
	func_memo = #{funcMemo},
	func_fee = #{funcFee}
	func_explain = #{funcExplain}
where func_name = #{funcName}

mapper.insert:
insert into level_info(
	level_name, level_memo, level_fee, level_vol
)values(
	#{levelName}, #{levelMemo}, #{levelFee}, #{levelVol}
)

mapper.update:
update level_info
	set
		level_memo = #{levelMemo},
		level_fee = #{levelFee},
		level_vol = #{levelVol}
where level_name = #{levelName}

mapper.select:
SELECT b.* FROM ( SELECT a.*, ROW_NUMBER() OVER (ORDER BY pay_date DESC) rnum FROM ( SELECT p.pay_code, p.pay_date, p.pay_edate, p.pay_amt, p.pay_method, p.pay_status, c.cus_rnum, c.cus_com, c.cus_name, pd.level_name, li.level_memo, li.level_fee, li.level_vol FROM payment p LEFT OUTER JOIN customer c ON p.cus_rnum = c.cus_rnum LEFT OUTER JOIN payment_detail pd ON p.pay_code = pd.pay_code LEFT OUTER JOIN level_info li ON pd.level_name = li.level_name INNER JOIN ( SELECT cus_rnum, pay_code, ROW_NUMBER() OVER (PARTITION BY cus_rnum ORDER BY pay_code DESC) AS rn FROM payment ) rn_subquery ON p.cus_rnum = rn_subquery.cus_rnum AND p.pay_code = rn_subquery.pay_code
WHERE 1=1 AND rn_subquery.rn = 1 ORDER BY p.pay_date DESC ) a
) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

select b.* from ( select a.*, row_number() over (order by a.inq_no desc) rnum from ( select i.inq_no, i.inq_title, i.cus_rnum, c.cus_name, c.cus_com, i.inq_date, i.inq_conf from inq_board i left outer join customer c on i.cus_rnum = c.cus_rnum where 1=1 and i.inq_conf in ('N', 'Y') order by case when i.inq_conf = 'N' then 1 when i.inq_conf = 'Y' then 2 else 3 end, i.inq_date desc ) a
) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}
